package com.kodnest.java;

public class Comparision {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		int c=10;
		System.out.println("a is equal to c "+(a==c));
		System.out.println("a is not equal to b "+(a!=b));
		System.out.println("a is greater then b "+(a>b));
		System.out.println("c is Less then b "+(c<b));
		System.out.println("a is Greater then or equal to b "+(a>=b));
		System.out.println("b is less then or equal to c "+(a<=b));
	}

}
